// Generated by CoffeeScript 1.7.1
var activeID, app, express, io, players, port;

express = require('express');

app = express();

port = 4839;

players = [];

activeID = 0;

app.set('views', "" + __dirname + "/tpl");

app.set('view engine', 'html');

app.engine('html', require('ejs').__express);

app.get('/', function(request, respond) {
  return respond.render('page');
});

app.use(express["static"]("" + __dirname + "/public"));

io = require('socket.io').listen(app.listen(port));

io.sockets.on('connection', function(socket) {
  socket.emit('message', {
    message: 'Welcome to <strong class=\"text-primary\">Hostile Takeover</strong>!'
  });
  socket.on('send', function(data) {
    if (data == null) {
      data = {};
    }
    return io.sockets.emit('message', data);
  });
  socket.on('game:reset', function(data) {
    if (data == null) {
      data = {};
    }
    players = [];
    return io.sockets.emit('board:update', {
      players: players
    });
  });
  socket.on('game:join', function(data) {
    var name, player;
    if (data == null) {
      data = {};
    }
    if (data.name) {
      name = data.name;
      player = {
        name: name,
        cards: ['/images/card_face_down.png', '/images/card_face_down.png'],
        credibility: 2
      };
      players.push(player);
      return io.sockets.emit('board:update', {
        players: players
      });
    }
  });
  return socket.on('game:start', function(data) {
    if (data == null) {
      data = {};
    }
    io.sockets.emit('game:start', data);
    io.sockets.emit('board:update', {
      players: players,
      activeSelector: "#player-" + activeID
    });
    return io.sockets.emit('message', {
      message: "<em><strong class=\"text-primary\">" + players[activeID].name + "</strong>'s turn</em>"
    });
  });
});

console.log("Listening on port " + port);
